package array_mainlecture8;

public class TwoDimensional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1. declaring the array - two dimensional array
		int ar[][] = new int[3][3]; //first size-row; second size-column
		//printing the values in 2-d array
		System.out.println(ar[0][0]);
		System.out.println(ar[0][1]);
		System.out.println(ar[0][2]);
		
		System.out.println(ar[1][0]);
		System.out.println(ar[1][1]);
		System.out.println(ar[1][2]);
		
		System.out.println(ar[2][0]);
		System.out.println(ar[2][1]);
		System.out.println(ar[2][2]);
		
		System.out.println("---------------------------------------");
		
		//printing in matrix format
		System.out.print(ar[0][0]);
		System.out.print(ar[0][1]);
		System.out.print(ar[0][2]);
		System.out.println();
		System.out.print(ar[1][0]);
		System.out.print(ar[1][1]);
		System.out.print(ar[1][2]);
		System.out.println();
		System.out.print(ar[2][0]);
		System.out.print(ar[2][1]);
		System.out.print(ar[2][2]);
		
	}

}
