package flowcontrols;

public class PatternEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	/*11111 5 row; 5 column
	 *22222
	 *33333
	 *44444
	 *55555
	 */
		
		for(int i=0; i<4; i++) 
		{
			for (int j=0; j<5; j++) 
			{
				System.out.print((i+1) + " "); //0+1=1; 1+1=2; 2+1=3;
			}
			System.out.println();
		
		  }
	}
	

}
